def rle(s):
    if not s: return ''
    result = []
    count = 1
    for i in range(1, len(s)):
        if s[i] == s[i-1]:
            count += 1
        else:
            result.append(f'{count}{s[i-1]}')
            count = 1
    result.append(f'{count}{s[-1]}')
    return ''.join(result)

print(rle('aaabbc'))  # 3a2b1c

def sum_1_to_10():
    total = 0
    for i in range(1, 11):
        total += i
    return total

print(sum_1_to_10())  # 55
